function abreOuFechaResposta(t){t.target.parentNode.classList.toggle("faq__pergunta__item--is--open")}function exibirHeader(){document.querySelector("header").classList.add("header--is-hidden")}function ocultarHeader(){document.querySelector("header").classList.remove("header--is-hidden")}function ocultarBotao(){const t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("arcos__tabs__button--is--active")}function ocultarPagina(){const t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("arcos__list--is--active")}document.addEventListener("DOMContentLoaded",(function(){const t=document.querySelectorAll("[data-tab-button]"),e=document.querySelectorAll("[data-faq-question]"),a=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",(function(){window.scrollY<a?ocultarHeader():exibirHeader()}));for(let e=0;e<t.length;e++)t[e].addEventListener("click",(function(t){const e=t.target.dataset.tabButton,a=document.querySelector(`[data-tab-id=${e}]`);ocultarPagina(),a.classList.add("arcos__list--is--active"),ocultarBotao(),t.target.classList.add("arcos__tabs__button--is--active")}));for(let t=0;t<e.length;t++)e[t].addEventListener("click",abreOuFechaResposta)}));