function ocultarBotao(){const t=document.querySelectorAll("[data-tab-button]");for(let a=0;a<t.length;a++)t[a].classList.remove("arcos__tabs__button--is--active")}function ocultarPagina(){const t=document.querySelectorAll("[data-tab-id]");for(let a=0;a<t.length;a++)t[a].classList.remove("arcos__list--is--active")}document.addEventListener("DOMContentLoaded",(function(){const t=document.querySelectorAll("[data-tab-button]");for(let a=0;a<t.length;a++)t[a].addEventListener("click",(function(t){const a=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${a}]`);ocultarPagina(),e.classList.add("arcos__list--is--active"),ocultarBotao(),t.target.classList.add("arcos__tabs__button--is--active")}))}));